script "add github to knownhosts" do
  interpreter "bash"
  user node[:myself]
  cwd "/Users/#{node[:myself]}/.ssh"
  code <<-EOH
    (cat /Users/#{node[:myself]}/.ssh/known_hosts | grep github.com) || \
    echo "github.com,207.97.227.239 ssh-rsa " >> /Users/#{node[:myself]}/.ssh/known_hosts 
  EOH
end

